//Подключаем API битрикса
require($_SERVER["DOCUMENT_ROOT"] . "/bitrix/modules/main/include/prolog_before.php");

//Заголовки
header('Cache-Control: no-cache, no-store, must-revalidate'); //Отключает кэширование
header('Expires: Mon, 26 Jul 1997 05:00:00 GMT'); //Дата и время после которой ответ считается устаревшим
header('Content-type: application/json'); // Тип возвращаемого контекта

//Отключаем статистику Bitrix
define("NO_KEEP_STATISTIC", true);
define("NOT_CHECK_PERMISSIONS", true);

$dataResult =  [
	'error' => false,
	'error_msg' => '',
	'result' => '',
];

try {
	$action = $_REQUEST['action'];
	if (empty($action) || !isset($action) || count($action) === 0) {
		throw new Exception('Действие не указано');
	}

	switch ($action) {
		case 'actionName' : include_once 'actionFileName.php'; break;
		default : throw new Exception('Обработка на указанное("'.$action.'"") действие отсутствует');
	}

} catch (Exception $e) {
	//Обработка ошибок
	$dataResult['error'] = true;
	$dataResult['error_msg'] = $e->getMessage();
}

//Отправка (вывод) ответа на frontend
echo json_encode($dataResult,JSON_UNESCAPED_UNICODE);

$arProperty = [
	'PROPERTY_NAME' => '',
];
$arFields = [
	'ACTIVE' => 'Y',
	'IBLOCK_SECTION_ID' => false,
	'PROPERTY_VALUES' => $arProperty,
	'IBLOCK_ID' => 0,
	'NAME' => 'Название элемента',
];
$newElement = new CIBlockElement();
$addResult = $newElement->Add($arFields); //https://dev.1c-bitrix.ru/api_help/iblock/classes/ciblockelement/add.php
if (!$addResult) {
	throw new Exception('Ошабка при добавление элемента. ' . $newElement->LAST_ERROR);
}

$arProperty = [
	'PROPERTY_NAME' => '',
];
$arFields = [
	'ACTIVE' => 'Y',
	'IBLOCK_SECTION_ID' => false,
	'PROPERTY_VALUES' => $arProperty,
	'IBLOCK_ID' => 0,
	'NAME' => 'Название элемента',
];
$updateElement = new CIBlockElement();
$updateResult = $updateElement->Update(0, $arFields); //https://dev.1c-bitrix.ru/api_help/iblock/classes/ciblockelement/update.php
if (!$updateResult) {
	throw new Exception('Ошабка при добавление элемента. ' . $updateElement->LAST_ERROR);
}
